import React, { Component } from 'react';

class Contact extends Component {
  render() {

    if(this.props.data){
      var name = this.props.data.name;
      var street = this.props.data.address.street;
      var city = this.props.data.address.city;
      var state = this.props.data.address.state;
      var zip = this.props.data.address.zip;
      var phone= this.props.data.phone;
      var email = this.props.data.email;
      var message = this.props.data.contactmessage;
    }

    return (
      <section id="contact">

         <div className="row section-head">

            <div className="two columns header-col">

               <h1 style={{"margin-left": "100%",}}><span>Get In Touch.</span></h1>

            </div>

            <div className="ten columns">

                  <p className="lead" style={{"margin-left": "10%","margin-top": "5%",}}>{message}</p>

            </div>

         </div>

         <div className="row">
            <div className="ten columns">

               <form action="" method="post" id="contactForm" name="contactForm">
					<fieldset>

                  <div>
						   <label htmlFor="contactName" style={{"text-align": "right","margin-right":"5%",}}>Name <span className="required">*</span></label>
						   <input type="text" defaultValue="" size="35" id="contactName" name="contactName" onChange={this.handleChange}/>
                  </div>

                  <div>
						   <label htmlFor="contactEmail" style={{"text-align": "right","margin-right":"5%",}}>Email <span className="required">*</span></label>
						   <input type="text" defaultValue="" size="35" id="contactEmail" name="contactEmail" onChange={this.handleChange}/>
                  </div>

                  <div>
						   <label htmlFor="contactSubject" style={{"text-align": "right","margin-right":"5%",}}>Subject</label>
						   <input type="text" defaultValue="" size="35" id="contactSubject" name="contactSubject" onChange={this.handleChange}/>
                  </div>

                  <div>
                     <label htmlFor="contactMessage" style={{"text-align": "right","margin-right":"5%",}}>Message <span className="required">*</span></label>
                     <textarea cols="50" rows="10" id="contactMessage" name="contactMessage"></textarea>
                  </div>

                  <div style={{"padding-right":"20%","padding-left":"12%",}}>
                     <button className="submit" style={{"width":"100%"}}>Submit</button>
                     <span id="image-loader">
                        <img alt="" src="images/loader.gif" />
                     </span>
                  </div>
					</fieldset>
				   </form>

           <div id="message-warning"> Error boy</div>
				   <div id="message-success">
                  <i className="fa fa-check"></i>Your message was sent, thank you!<br />
				   </div>
           </div>
      </div>
   </section>
    );
  }
}

export default Contact;
