import React from "react";
import Tabs from "@material-ui/core/Tabs";
import Tab from "@material-ui/core/Tab";
import Typography from "@material-ui/core/Typography";
import { withStyles } from "@material-ui/core/styles";


class ProfileTabs extends React.PureComponent {
  state = { activeIndex: 0};

  handleChange = (_, activeIndex) => this.setState({ activeIndex });
  render() {
    if(this.props.data){
          var skillsTitle = this.props.data.skills.map(skills=>{
            return <MyTab label={skills.name}/>
          })
          var i = 0;
          var skillsContent = this.props.data.skills.map(skills=>{
          const { activeIndex } = this.state;
          return activeIndex === i++ && <TabContainer>{skills.level}</TabContainer>}
          )
          }
    const { activeIndex } = this.state;
    return (
      <div
        style={{
          display: "flex",
          "margin-left": "0px",
        }}
      >
        <div class = "columns" style ={{"padding-left": "0px",}}>
        <VerticalTabs indicatorColor={""}  value={activeIndex} onChange={this.handleChange}>{skillsTitle}</VerticalTabs>
        </div>
        <p>{skillsContent}</p>
      </div>
    );
  }
}

const VerticalTabs = withStyles(theme => ({
  root: {
    "font-size":"24px",
    "margin-left": "0px"},
  flexContainer: {
    flexDirection: "column"
  },
  indicator: {}
}))(Tabs);

const Typo = withStyles(theme => ({
  root: {
    "white-space": "pre-wrap",
    "position": "relative",
    "font-size": "14px",
    "padding": "0px",
    "font-style": "librebaskerville-bold",
  },}))(Typography);


const MyTab = withStyles(theme => ({
  root: {
    backgroundColor: null,
    "font-size":"12px",
    "font-family": "librebaskerville-bold",
    "font-align": "left",
    color: "#34495e",
    padding: "0px 0px",
    borderRadius: theme.shape.borderRadius,
    "margin-bottom":"10%"
  },
  wrapper: {
    color: "#fff",
    "font-family": "librebaskerville-bold",
    backgroundColor: "#F1B24A",
    padding: theme.spacing(2)
  },
  selected: {
    color: "#fff",
    backgroundColor: "#fff",
  }
}))(Tab);

function TabContainer(props) {
  return (
    <Typo component="div">
      {props.children}
    </Typo>
  );
}

export default ProfileTabs;
